<template>
  <div>
    <v-app-bar color="primary accent-4" dense dark>
      <v-toolbar-title>
        <v-btn icon>
          <v-icon>mdi-home-variant</v-icon>
        </v-btn>
      </v-toolbar-title>
      <v-spacer></v-spacer>
      <v-menu left bottom>
        <template v-slot:activator="{ on, attrs }">
          <v-btn icon v-bind="attrs" v-on="on">
            <v-avatar v-if="!account.status.loggingOut && avatar !== ''">
              <img :src="`${url}avatars/${avatar}`" />
            </v-avatar>
            <img
              v-else-if="account.status.loggingOut"
              class="mx-3"
              src="data:image/gif;base64,"
            />
          </v-btn>
        </template>

        <v-list>
          <v-list-item @click="Logout">
            <v-list-item-title>Logout</v-list-item-title>
          </v-list-item>
        </v-list>
      </v-menu>
    </v-app-bar>
    <div class="container">
      <h1 v-if="account.user.userName">Hey Heey HEEEYY {{ account.user.userName }}</h1>
      <img
        v-else
        src="data:image/gif;base64,"
      />
    </div>
  </div>
</template>

<script>
import { mapState, mapActions } from 'vuex';
export default {
  data: () => {
    return { url: process.env.VUE_APP_API_URI, avatar: '' };
  },
  computed: {
    ...mapState({
      account: (state) => state.account,
    }),
  },
  methods: {
    ...mapActions('account', ['getCurrentUser', 'logout']),
    Logout() {
      this.logout();
    },
  },
  created() {
    this.getCurrentUser().then(() => {
      const avatar = this.account.user.avatar;
      const lastIndex = avatar.lastIndexOf('avatars');
      this.avatar = this.account.user.avatar.substring(lastIndex + 8);
    });
  },
  mounted() {},
};
</script>
